class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        const int n = c.size();

        std::vector<int> a;
        a.insert(a.end(), c.begin(), c.end());
        a.insert(a.end(), c.begin(), c.end());

        int ans = 0, prev = a[0] ^ 1, start = 0;
        for (int i = 0; i < n + n; ++i) {
            if (a[i] == prev) {
                start = i;
            }
            prev = a[i];

            while (start <= i - n) ++start;
            if (i >= n && i - start + 1 >= k) ++ans;

            // std::cout << i << ": " << i << " " << i - start + 1 << std::endl;
        }

        return ans;
    }
};
