class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        n *= 2;
        int ans = 0;
        vector<int>prf(n + 1);
        for(int i = 0; i < n; ++i)
            colors.push_back(colors[i]);

        for(int i = 1; i < n; ++i)
            prf[i] = prf[i - 1] + (colors[i] != colors[i - 1]);

        for(int i = k - 1, p = 0; p < n / 2; ++i, ++p){
            ans += prf[i] - prf[i - k + 1] == k - 1;
        }
        return ans;
    }
};
