class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int n = colors.length;
        int[] col = new int[n + k - 1];
        for (int i = 0; i < n + k - 1; i++) {
            col[i] = colors[i % n];
        }
        int cur = 1, res = 0;
        n = n + k - 1;
        for (int i = 1; i < n; i++) {
            if (col[i] != col[i - 1]) {
                cur++;
            } else {
                cur = 1;
            }
            if (cur >= k) {
                res++;
            }
        }
        return res;
    }
}
