class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& rang, int k) {
          int N = rang.size();
        int ginteKiSankhya = 0;

        if (k == 1) {
            // Har ek tile ek alag-alag samuh hai
            return N;
        }

        int parivartanLambai = 1;
        int ankur = 1;
        while (ankur < N + k - 1) {
            int vartaman = ankur;
            int purv = ankur - 1;

            // Golakar purnaankn
            if (vartaman >= N) {
                vartaman -= N;
            }
            if (purv >= N) {
                purv -= N;
            }

            if (rang[vartaman] != rang[purv]) {
                ++parivartanLambai;
            } else {
                parivartanLambai = 1;
            }

            // Yadi humein k lambai ke ek moolyavan samuh hai, toh uska chek karen
            if (parivartanLambai >= k) {
                ++ginteKiSankhya;
            }

            ++ankur;
        }

        return ginteKiSankhya;
    }
};
