class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        n_colors = [(colors[i] + i) % 2 for i in range(n)]
        arr = []
        cur_val = n_colors[0]
        cur_len = 1
        for c in n_colors[1:]:
            if c == cur_val:
                cur_len += 1
            else:
                cur_val = c
                arr.append(cur_len)
                cur_len = 1
        if len(arr) == 0:
            if n % 2 == 0:
                return n
            else:
                return n - k + 1
        if (cur_val == colors[0]) == (n % 2 == 0):
            arr[0] += cur_len
        else:
            arr.append(cur_len)
        res = 0
        for a in arr:
            if a >= k:
                res += a - k + 1
        return res
