class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int ans=0,n=colors.size(); 
        vector<int> v=colors;
        for(int i=0;i<n;i++)v.push_back(colors[i]);
        int l=1,r=2;
        while(r<=n-1+k)
        {
            while(r<=n-1+k && r<l+k-1 && v[r]!=v[r-1])r++;
            // cout<<"r="<<r<<endl;
            if(r<=n-1+k && l+k-1==r && v[r]!=v[r-1])
            {
                ans++;
                // cout<<"l="<<l<<" r="<<r<<endl;
                l++,r++;
            }
            else 
            {
                l=r;r++;
            }
        }
        return ans;
    }
};
