class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) {
        int ans=0;
        if(k>v.size()){
            return ans;
        }
        int n=v.size();
        vector<int>a(2*n);
        for(int i=0;i<n;i++){
            a[i]=v[i];
            a[n+i]=v[i];
        }
        n+=k;
        int cnt=0;
        vector<int>diff(n+5,0);
        for(int i=1;i<n;i++){
            diff[i]+=diff[i-1]+abs(a[i]-a[i-1]);
        }
        for(int i=k;i<n;i++){
            if(diff[i]-diff[i-k+1]==k-1){
                cnt++;
            }
        }
        return cnt;

    }
};
