class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        for (int i = 0; i < k - 1; i++) {
            colors.push_back(colors[i]);
        }

        int ans = 0;
        int l = 0;
        int r = 1;
        while (l <= colors.size() - k) {
            if (colors[r] != colors[r - 1]) {
                if (r - l + 1 == k) {
                    ans++;
                    l++;
                }
            } else {
                l = r;
            }
            r++;
        }
        return ans;
    }
};
