class Solution {
public:
int numberOfAlternatingGroups(vector<int>& colors, int k) {
    int len=colors.size();
    int res=0;
    vector<int>h1(len,1);
    vector<int>h2(len,2);
    for(int i=1;i<len;++i){
        if(colors[i]!=colors[i-1]){
            h1[i]=h1[i-1]+1;
        }
    }

    if(colors[0]==colors.back()){
        for(auto v:h1){
            if(v>=k){
                ++res;
            }
        }
    }else{
        for(int i=0;i<len;++i){
            if(h1[i]==i+1 && h1[i]+h1.back()>=k)++res;
            else if(h1[i]>=k)++res;
        }
    }
    return res;

}

};
