class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:

        c = colors[:]
        dp = [0] * len(c)
        for i in range(len(c)):
            dp[i] = 1
            if i>0 and c[i]!=c[i-1]:
                dp[i] = dp[i-1]+1

        c2 = c + c
        dp2 = [0] * len(c2)
        for i in range(len(c2)):
            dp2[i] = 1
            if i>0 and c2[i]!=c2[i-1]:
                dp2[i] = dp2[i-1]+1


        res1 = sum(x>=k for x in dp)
        res2 = sum(x>=k for x in dp2)
        #print(dp, dp2)
        #print(res1, res2)
        return res2-res1
