class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int ans = 0;
        int cc = 0;
        for (int i = 0; i < n + k - 3; i ++) {
            if (colors.at(i % n) != colors.at((i+1) % n) && colors.at((i+2)%n) != colors.at((i+1) % n)) {
                cc ++;
                if (cc >= k - 2) {
                    ans++;
                }
            } else {
                cc = 0;
            }
        }
        return ans;
    }
};
