from collections import defaultdict

class Solution:
    def numberOfAlternatingGroups(self, colors, k):
        colors += colors[:k]
        n = len(colors)
        ans = 0
        color_to_index = defaultdict(int)
        prefixArray = [0] * (n + 1)

        for i in range(1, n):
            prefixArray[i] = prefixArray[i - 1] + abs(colors[i] - colors[i - 1])

        k -= 1

        for i in range(k + 1, n):
            if prefixArray[i] - prefixArray[i - k] == k:
                ans += 1

        return ans
