class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n=len(colors)
        colors+=colors
        cnt=0
        res=0
        for i in range(n+k-1):
            if i==0 or colors[i]!=colors[i-1]:
                cnt+=1
            else:
                cnt=1
            if cnt>=k:
                res+=1
        return res
