#include <vector>
using namespace std;

class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        if (k > n){
             return 0;
        }  
        if (k == 1) {
            return n; 
        }

        int count = 0;
        int valid = 0;   
        for (int i = 0; i < k; ++i) {
            if (i > 0 && colors[(i - 1 + n) % n] != colors[i]) {
                valid++;
            }
        }

        if (valid == k - 1) {
            count++;
        }

        for (int i = 1; i < n; ++i) {
            int j = (i + k - 1) % n;          
            int x = (i - 1) % n;        
            int y = (j - 1 + n) % n;        

            if (colors[x] != colors[(x + 1) % n]) {
                valid--;
            }
            if (colors[y] != colors[j]) {
                valid++;
            }

            if (valid == k - 1) {
                count++;
            }
        }

        return count;
    }
};
