#define ll long long
#define pb push_back
#define endl "\n"
#define siz(a) int((a).size())
//using namespace std;

class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector <int> bcol;
        bcol = colors;
        int n = siz(colors);
        for(int i = 0; i < n; i++) {
          bcol.pb(colors[i]);
        }
        int ans = 0;
        int cur = bcol[0];
        int len = 1;

        for(int i = 1; i < n + k - 1; i++) {
          if (cur != bcol[i]) {
            len += 1;
            cur ^= 1;
          } else {
            ans += max(0, len - k + 1);
            len = 1;
            cur = bcol[i];
          }
        }
        ans += max(0, len - k + 1);
        return ans;
    }
};
