class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        colors.extend(colors)
        result = 0
        alt = 1
        last = colors[0]
        for i in range(1, n+k-1):
            if colors[i] == 1-last:
                alt += 1
                if alt >= k:
                    result += 1
                last = 1-last
            else:
                alt = 1
        return result
