class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int cnt = 0;
        int n = colors.size();
        vector<int> dp(n,1);
        for(int i=1; i<n+k-1 ;i++){
            if(colors[(i-1)%n] != colors[i%n]){
                dp[i%n] = dp[(i-1)%n]+1;
            }else{
                cnt += (dp[(i-1)%n]<k ? 0 : dp[(i-1)%n]-k+1);
            }
        }
        cnt += (dp[(n+k-2)%n]<k ? 0 : dp[(n+k-2)%n]-k+1);
        return cnt;
    }
};
