class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        vector<int> v = c;
        int n = c.size();
        for(int i = 0 ; i < n ; i++){
             v.push_back(c[i]);
        }
        vector<int> p(2*n , 0);
        for(int i = 1 ; i < 2*n ; i++){
            if(v[i] != v[i-1]){
                p[i] = 1;
            }
        }
        vector<int> next(2*n , 2*n);
        for(int i = 2*n-2;i>=0;i--){
              if(p[i] == p[i+1]){
                  next[i] = next[i+1];
              }else{
                  next[i] = i+1;
              }
        }
        int ans = 0;
        for(int i = 0 ; i < n ;i++){
            if(p[i+1] == 1 and next[i+1] >= i+k)ans++;
        }
        return ans;
    }
};
