template<typename T>
struct Fenwick {
  int n;
  vector<T> tree;
  Fenwick(int n) : n(n), tree(n) {}

  void add(int x, T v) {
    for (int i = x + 1; i <= n; i += i & -i) {
      tree[i - 1] += v;
    }
  }

  // [0, x)
  T query(int x) {
    T ans{};
    for (int i = x; i > 0; i -= i & -i) {
      ans += tree[i - 1];
    }
    return ans;
  }

  // [l, r)
  T range(int l, int r) {
    return query(r) - query(l);
  }
};


class Solution {
public:
  int numberOfAlternatingGroups(vector<int>& colors, int k) {
    int n = colors.size();
    vector<int> nums(2 * n);
    for (int i = 0; i < 2 * n; i++) {
      nums[i] = colors[i % n];
    }
    int ans = 0;
    int beg = 0;
    while (beg < n) {
      int end = beg;
      while (end + 1 < n + k - 1 && nums[end + 1] != nums[end]) {
        end += 1;
      }
      int len = end - beg + 1;
      if (len >= k) {
        ans += len - k + 1;
      }
      beg = end + 1;
    }
    return ans;
  }
};
