class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        colors += colors
        res, l = 0, 0
        for r in range(1, n + k - 1):
            if colors[r] == colors[r - 1]:
                l = r
            if r - l + 1 >= k:
                res += 1
        return res
