class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        if (n < k) return 0; 

        vector<int> a(colors.begin(), colors.end());
        a.insert(a.end(), colors.begin(), colors.begin() + k - 1);
        int ans = 0; int cnt = 1;
        for (int i = 1; i < a.size(); ++i) {
            if (a[i] != a[i - 1]) {
                ++cnt;
            } else {
                cnt = 1;
            }
            if (cnt >= k) {
                ++ans;
                cnt = k - 1;
            }
        }

        return ans;
    }
};
