class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        queue<int> q;
        q.push(colors[colors.size() - k + 1]);
        int alt = 0;
        for(int i = colors.size() - k + 2; i < colors.size(); i++){
            if(colors[i] != q.back()) alt++;
            q.push(colors[i]);
        }

        int ans = 0;
        for(int i = 0; i < colors.size(); i++){
            if(colors[i] != q.back()) alt++;
            q.push(colors[i]);
            if(alt >= k - 1) ans++;
            int front = q.front();
            q.pop();
            if(front != q.front()) alt--;
        }
        return ans;
    }
};
