class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int n=c.size();
        int ans=0;
        int cnt=1;
        for(int i=1;i<n+k-1;i++){
            int  x=i-1, y=i;
            if(y>=n) y=y-n;
            if(x>=n) x=x-n;
            if(c[y]!=c[x]) cnt++;
            else cnt=1; 
            if(cnt>=k) ans++;
        } 
        return ans;
    }
};
