class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int n = c.size(), cnt = 0, match = 1;
        vector<int>v=c;
        for(auto i:c)v.push_back(i);
        set<int>s;
        for(int i=0;i<v.size()-1;i++)if(v[i]==v[i+1])s.insert(i);
        for(int i=0;i<n;i++){
            int j=i;
            //j,j+1,j+2,j+k-1;
            auto lb=s.lower_bound(j);
            if(lb==s.end())cnt++;
            else{
                int x=*lb;
                if(x<=j+k-2)continue;
                else cnt++;
            }
        }
            return cnt;
    }
};
