class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& cols, int size) {
        long finals = cols.size();
        long gain = 0;

        if (size == 1) {
            return finals;
        }

        int altLength = 1;
        int idx = 1;
        while (idx < finals + size - 1) {
            int curr = idx;
            int prev = idx - 1;

            if (curr >= finals) {
                curr -= finals;
            }
            if (prev >= finals) {
                prev -= finals;
            }

            if (cols[curr] != cols[prev]) {
                ++altLength;
            } else {
                altLength = 1;
            }

            if (altLength >= size) {
                ++gain;
            }

            ++idx;
        }

        return gain;
    }
};
