class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        left = 0 
        n = len(colors)
        ans = 0
        for right in range(n + k - 1):
            if right > 0 and colors[(right - 1) % n] == colors[right % n]:
                left = right 
            if right - left + 1 == k:
                ans += 1
                left += 1
        return ans
