from typing import List

class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        if k == 1:  
            return len(colors)

        n = len(colors)
        count = 0
        alternates = 0

        for i in range(1, k):
            if colors[i] != colors[i-1]:
                alternates += 1

        if alternates == k - 1:
            count += 1

        for i in range(k, n + k - 1):
            if colors[i % n] != colors[(i - 1) % n]:
                alternates += 1
            if colors[(i - k + 1) % n] != colors[(i - k) % n]:
                alternates -= 1

            if alternates == k - 1:
                count += 1

        return count
