#include <vector>
using namespace std;

class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& A, int k) {
        int N = A.size();
        int count = 0;
        if (k == 1){
            return N;
        }
        int c = 1;
        for (int i = 1; i < N + k - 1; ++i) {
            if (A[i % N] != A[(i - 1) % N]) {
                ++c;
            } else {
                c = 1;
            }
            if (c >= k) {
                ++count;
            }
        }

        return count;
    }
};
