class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        length = 1
        result = 0

        pref = [0]
        for i in range(n):
            temp2 = pref[-1] + colors[i]
            temp2 *= 2
            pref.append(temp2)

        for i in range(1, n + k - 1):
            if colors[i % n] == colors[(i - 1) % n]:
                temp = max(0, (length-k+1))
                result += max(0, temp)
                length = 1
            else:
                length += 1
        temp = max(0, (length-k+1))
        result += max(0, temp)
        return result
