class Solution {
    public int numberOfAlternatingGroups(int[] a, int k) {
        int c = 1, n = a.length, ans = 0;
        for (int i = n - k + 2; i < n; ++i)
            if (a[i] != a[i - 1]) ++c;
            else c = 1;
        if (a[0] != a[n - 1]) ++c;
        else c = 1;
        if (c == k) ++ans;
        for (int i = 1; i < n; ++i) {
            if (a[i] != a[i - 1]) ++c;
            else c = 1;
            if (c >= k) ++ans;
        }
        return ans;
    }
}
