public class Solution {
    public int NumberOfAlternatingGroups(int[] colors, int k) {
        List<int> t = new List<int>(colors);
        t.AddRange(colors[..k]);
        int n = t.Count;
        int count = 0;
        Dictionary<string, int> colorToIndex = new Dictionary<string, int>();
        int[] pre = new int[n + 1];
        for (int i = 1; i < n; ++i) {
            pre[i] = pre[i - 1] + Math.Abs(t[i] - t[i - 1]);
        }
        k--;
        for (int i = k + 1; i < n; ++i) {
            if (pre[i] - pre[i - k] == k) {
                count++;
            }
        }
        return count;
    }
}
