class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
           for(int i=0;i<k-1;i++)c.push_back(c[i]);
           int n=c.size();
           int ans=0;
           int count=1;
           for(int i=1;i<n;i++){
              if(c[i]!=c[i-1])count++;
              else{
                ans+=max(count-k+1,0);
                count=1;
                cout<<count<<endl;
              }
           }
           ans+=max(count-k+1,0);
           return ans;
    }
};
