class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int ans = 0, n = colors.size();
        vector<bool> ck(n, true);
        for(int i = 1; i < colors.size(); i++)
        {
            if(colors[i] == colors[i-1])
            {
                ck[i] = false;
            }
        }
        int c = 0, fc = 0;
        for(int i = 0; i < n; i++)
        {
            if(ck[i])
            {
                c++;
            }
            else
            {
                // cout << c << "\n";
                if(fc == 0)
                {
                    fc = c;
                }
                ans += max(c-k+1, 0);
                c = 1;
            }
        }
        // cout << c << "\n";
        if(fc == 0)
        {
            fc = c;
        }
        if(colors[n-1] != colors[0])
        {
            ans += min(max(c+fc-k+1, 0), c);   
        }
        else
        {
            ans += max(c-k+1, 0);
        }
        return ans;
    }
};
