class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        colors = colors + colors[:k-1]
        ans = 0
        count = 0
        for idx in range(1, len(colors)):
            if colors[idx] != colors[idx - 1]:
                count += 1
                if count >= k - 1:
                    ans += 1
            else:
                count = 0
        return ans
