class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n=a.size();
        vector<int>pre(n,0);
        for(int i=0;i<n;i++)
        {
             int inp=(i+1)%a.size();
            int inm=(i-1+a.size())%a.size();
            if(a[inp]!=a[i]&&a[inm]!=a[i])
            {
               pre[i]=1;
            }


        }
        int ans=0;
        int su=0;
         k-=2;
        // for(int i=0;i<n;i++)
        // {
        //     cout<<pre[i]<<" ";
        // }
        // cout<<endl;
        for(int i=0; i<k;i++)
        {
            su+=pre[i];

        }

        if(su==k)
        {
            ans++;
        }
        for(int i=1;i<n;i++)
        {
            su+=pre[(i+k-1)%n];
            su-=pre[i-1];
            if(su==k)
        {
            ans++;
        }

        }
        return ans;


    }
};
