class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int ans = 0;
        int count = 0, n = colors.size();
        for(int i = 0; i < n; i++)
            colors.push_back(colors[i]);
        for(int i = 0; i < k - 1; i++)
        {
            if(colors[i] != colors[i + 1])
                count++;
        }
        ans += (count == k - 1);
        for(int i = k; i <= n - 1 + k - 1; i++)
        {
            if(colors[i] != colors[i - 1])
                count++;
            if(colors[i + 1 - k] != colors[i - k])
                count--;
            ans += (count == k - 1);
        }

        return ans;
    }
};
