class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int check1=1;
        int check2=0;
        int it;
        int n=colors.size();

        for (int i=1; i<n+k-1; i++) {
            it=i%n;
            if(colors[(it-1+n)%n]!=colors[it]) check1++;
            else check1=1;

            if(check1>=k){
                check1=k-1;
                check2++;
            }
        }
        return check2;
    }
};
