class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& V, int k) {
        int n = V.size();

        int i=0, j;
        int count = 0;
        while(i<n) {
            int j=i+1;
            while(j-i<k-1 && V[j%n]!=V[(j-1)%n]){
                // cout<<j<<" ";
                j++;
            }
            // cout<<"\n\n";
            if(j-i<k-1){
                i = j;
                continue;
            }
            while(i<n && V[j%n]!=V[(j-1)%n]){
                // cout<<i<<" "<<j<<"\n";
                count++;
                i++;
                j++;
            } 
            i = j;
        }
        return count;
    }
};
