class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int count = 0, n = colors.size();
        for(int left = 0, right = 1; left < n; ++left) {
            right = max(right, left + 1);
            while(right - left < k) {
                if(colors[right % n] == colors[(right - 1) % n]) {
                    break;
                }
                right++;
            }

            if(right - left == k) {
                count++;
            }
        }

        return count;
    }
};
