class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        pos = 0
        while pos < n:
            if colors[pos] == colors[(pos+1)%n]:
                break
            pos += 1
        if pos == n:
            return n

        res = 0
        colors += colors
        end = pos + n
        pos += 1
        L = 0
        while pos <= end:
            if colors[pos] != colors[pos-1]:
                L += 1
            else:
                L = 1
            if L >= k:
                res += 1
            pos += 1
        return res
