class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        int dp[n];
        memset(dp,0,sizeof(dp));
        dp[n-1]=1;
        for(int i=n-2;i>=0;i--){
            if(colors[i]!=colors[i+1]){
                dp[i]=dp[i+1]+1;
            }
            else{
                dp[i]=1;
            }
        }
        int ans=0;
        for(int i=0;i<n;i++){
            if(i+k-1<n){
                ans+=(dp[i]>=k);
            }
            else{
                int vk=dp[i];
                int vk1=dp[0];
                if(vk==(n-i)&&vk+vk1>=k&&colors[n-1]!=colors[0]) ans+=1;
            }
        }
        return ans;
    }
};
