class Solution
{
public:
    int numberOfAlternatingGroups(vector<int> &arr, int k)
    {
        int n = arr.size();
        int ans = 0;
        if (k == 1)
        {
            return n;
        }
        int i = 1, altLen = i;
        while (i <= n + k - 2)
        {
            int j = i;
            int l = j - 1;

            if (j >= n)
            {
                j = j % n;
            }
            if (l >= n)
            {
                l = l % n;
            }

            if (arr[j] != arr[l])
            {
                altLen++;
            }
            else
            {
                altLen = 1;
            }

            if (altLen >= k)
            {
                ans++;
            }
            i++;
        }

        return ans;
    }
};
