class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        int cnt=0;
        if(k==1)return n;
        int i=1,len=1;
        while(i<n+k-1){
            int cur=i,p=i-1;
            if(cur>=n)cur-=n;
            if(p>=n)p-=n;
            if(colors[cur]!=colors[p])len++;
            else len=1;
            if(len>=k)cnt++;
            i++;
        }
        return cnt;

    }
};
