class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size() ;
        int cnt = 1 ;
        int ans = 0 ;
        for (int i=1 ; i<n+k-1 ; ++i) {
            int a = i%n , b=(i-1+n)%n ;
            if (colors[a]==colors[b]) {
                cnt= 1 ; 
                continue ;
            }
            ++cnt ;
            ans += (cnt>=k);

        }
        return ans ;
    }
};
