class Solution {
public:

    void add_back(vector<int>& a, int k) {
    int n = a.size();
      a.resize(n + k - 1);

    for (int i = 0; i < k - 1; ++i) {
        a[n + i] = a[i];
    }
}

    int solve(vector<int>& a, int k) {
         add_back(a,k);

        int v = 0, v1 = 1;
        int i=1;

        while (i<a.size() ) {
            if (a[i] != a[i - 1]) {
                v1 += 1;
            } else {
                v1 = 1;
            }

            if (v1 >= k) {
                v++;
            }
            i++;
        }
        return v;
    }
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int a=solve(colors,k);
        return a;
    }
};
