class Solution {
    public int numberOfAlternatingGroups(int[] arr, int k) {
        int cnt=1;int ans=0,n=arr.length;
       for(int i=1;i<arr.length;i++)
       {
           if(arr[i]!=arr[i-1])
               cnt++;
           else{
               if(cnt>=k)
               ans+=(cnt-k+1);
               cnt=1;
           }
       }
        if(cnt>=k) ans+=(cnt-k+1);
       ArrayList<Integer> al=new ArrayList<>();
        for(int i=n-k+1;i<n;i++) al.add(arr[i]);
        for(int i=0;i<k-1;i++) al.add(arr[i]);
        cnt=1;
         for(int i=1;i<al.size();i++)
       {
           if(al.get(i)!=al.get(i-1))
               cnt++;
           else{
               if(cnt>=k)
               ans+=(cnt-k+1);
               cnt=1;
           }
       }
        if(cnt>=k) ans+=(cnt-k+1);
        return ans;
    }
}
