class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) {
        int ret = 0;
        int bad = -10000000;
        for (int i=0;i<sz(v) + k - 1;++i) {
            int pos = i % sz(v);
            int prev = ((i-1) + sz(v)) % sz(v);
            if (i) {
                if (v[pos] == v[prev]) {
                    bad = i-1;
                }
            }
            //cout << i << " " << bad << " " << k << endl;
            if (i - bad >= k && i >= (k-1)) ++ret;
        }
        return ret;
    }
};
