class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        c=0
        for i in range(k-1):
            colors.append(colors[i])
        l,r=0,0
        last = colors[0]
        #print(colors)
        while r < len(colors) and l < len(colors):
            while r < len(colors)-1 and r-l < k-1:
                r+=1
                if colors[r-1] == colors[r]:
                    l = r
                    #print(l)
                last = colors[r]
            if l > len(colors)-k:
                break
            #print(colors[l:r+1], l, r)
            c+=1
            l+=1
            if r < len(colors)-1:
                if colors[r] == colors[r+1]:
                    l = r
                else:
                    r+=1
            else:
                r+=1
            #print(l,r)
        return c
