class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {

        int count=1,res= 0;
        int n=colors.size();

        for(int i=1;i<colors.size()+k-1; i++){
            int j= i% n;

            if(colors[j]!=colors[(j- 1 +n)% n]){
                count++;
             }
            else count= 1;
            if (count>=k) res++;
        }
        return res;

    }

};
