class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        vector<int>len(n,1);
        for(int i=1;i<n;i++)
        {
            if(colors[i]!=colors[i-1]) 
                len[i] += len[i-1];
        }
        int ans=0;
        for(int i=0;i<n;i++)
        {
            if(i+k<=n){
                if(len[i+k-1]-len[i]==k-1)
                    ans++;
            }
            else{
                if(colors[n-1]!=colors[0] and len[n-1]-len[i]+len[(i+k-1)%n]==k-1)
                    ans++;
            }
        }
        return ans;
    }
};
